import { assertEquals } from "asserts";

import fractionToDecimal from "./index.ts";

Deno.test("fraction-to-recurring-decimal", () => {
    assertEquals(
        [
            "1.()",
            "0.99995",
        ],
        [[20000, 19999], [19999, 20000]].map((a) =>
            fractionToDecimal(a[0], a[1])
        ),
    );
});
Deno.test("fraction-to-recurring-decimal", () => {
    assertEquals(
        [
            "0.5",
            "2",
            "0.(012)",
            "0.(523809)",
            "1.(90)",
            "0.(123)",
            "0.(523809)",
            "1.(90)",
            "2.05",
            "0.(213)",
        ],
        [
            [1, 2],
            [2, 1],
            [4, 333],
            [11, 21],
            [21, 11],
            [41, 333],
            [11, 21],
            [21, 11],
            [41, 20],
            [71, 333],
        ].map((a) => fractionToDecimal(a[0], a[1])),
    );
});
